data.customCss:
.hero h1 {
    text-align: center;
}

.tips-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
    margin: 2rem 0 4rem 0;
}

.tip-card {
    background: #ffffff;
    padding: 2rem;
    border-radius: 12px;
    box-shadow: 0 2px 8px rgba(0,0,0,0.1);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.tip-card:hover {
    transform: translateY(-4px);
    box-shadow: 0 4px 16px rgba(0,0,0,0.15);
}

.tip-icon {
    font-size: 2.5rem;
    color: #2B7BD3;
    margin-bottom: 1rem;
}

.tip-card h3 {
    color: #2c3e50;
    font-size: 1.3rem;
    margin-bottom: 1rem;
    font-weight: 700;
}

.tip-card p {
    color: #666;
    line-height: 1.6;
    margin: 0;
}

.tip-card a {
    color: #2B7BD3;
    text-decoration: none;
    border-bottom: 1px dotted #2B7BD3;
}

.tip-card a:hover {
    border-bottom-style: solid;
}

.support-section h2 {
    margin-top: 3rem;
    margin-bottom: 1rem;
}

@media (max-width: 768px) {
    .tips-grid {
        grid-template-columns: 1fr;
        gap: 1.5rem;
    }

    .tip-card {
        padding: 1.5rem;
    }
}

@media (prefers-color-scheme: dark) {
    .tip-card {
        background: #2d2d2d;
        box-shadow: 0 2px 8px rgba(0,0,0,0.3);
    }

    .tip-card:hover {
        box-shadow: 0 4px 16px rgba(0,0,0,0.4);
    }

    .tip-icon {
        color: #5BA4FF;
    }

    .tip-card h3 {
        color: #ffffff;
    }

    .tip-card p {
        color: #cccccc;
    }

    .tip-card a {
        color: #5BA4FF;
        border-bottom-color: #5BA4FF;
    }

    .tip-card a:hover {
        color: #7CB9FF;
        border-bottom-color: #7CB9FF;
    }
}

