data.santaScript:
// Santa Party Mode with Techno Jingle Bells
let santaAudioContext = null;
let santaIsPlaying = false;
let santaOscillators = [];

// Clean up audio when page is shown from back-forward cache
window.addEventListener('pageshow', function(event) {
    if (event.persisted) {
        // Page was restored from bfcache - stop any playing audio and reset state
        stopJingleBells();
        const santa = document.querySelector('.dancing-santa');
        if (santa) santa.classList.remove('party-mode');
    }
});

// Also stop when leaving the page
window.addEventListener('pagehide', function() {
    stopJingleBells();
});

function toggleSantaParty(element) {
    // Always stop existing audio first to prevent overlapping
    stopJingleBells();

    element.classList.toggle('party-mode');

    if (element.classList.contains('party-mode')) {
        playJingleBells();
    }
}

function playJingleBells() {
    if (santaIsPlaying) return;
    santaIsPlaying = true;

    try {
        santaAudioContext = new (window.AudioContext || window.webkitAudioContext)();

        // Jingle Bells melody - classic style
        const melody = [
            { note: 'E5', duration: 0.25 },
            { note: 'E5', duration: 0.25 },
            { note: 'E5', duration: 0.5 },
            { note: 'E5', duration: 0.25 },
            { note: 'E5', duration: 0.25 },
            { note: 'E5', duration: 0.5 },
            { note: 'E5', duration: 0.25 },
            { note: 'G5', duration: 0.25 },
            { note: 'C5', duration: 0.35 },
            { note: 'D5', duration: 0.15 },
            { note: 'E5', duration: 0.8 },
            { note: 'F5', duration: 0.25 },
            { note: 'F5', duration: 0.25 },
            { note: 'F5', duration: 0.35 },
            { note: 'F5', duration: 0.15 },
            { note: 'F5', duration: 0.25 },
            { note: 'E5', duration: 0.25 },
            { note: 'E5', duration: 0.25 },
            { note: 'E5', duration: 0.25 },
            { note: 'E5', duration: 0.25 },
            { note: 'D5', duration: 0.25 },
            { note: 'D5', duration: 0.25 },
            { note: 'E5', duration: 0.25 },
            { note: 'D5', duration: 0.5 },
            { note: 'G5', duration: 0.5 },
        ];

        const noteFreq = {
            'C5': 523.25, 'D5': 587.33, 'E5': 659.25, 'F5': 698.46, 'G5': 783.99, 'A5': 880.00
        };

        let startTime = santaAudioContext.currentTime;

        function playMelodyLoop() {
            if (!santaIsPlaying) return;

            melody.forEach((item) => {
                // Main melody - soft triangle wave
                const osc = santaAudioContext.createOscillator();
                const gain = santaAudioContext.createGain();
                osc.type = 'triangle';
                osc.frequency.value = noteFreq[item.note];
                gain.gain.setValueAtTime(0.12, startTime);
                gain.gain.exponentialRampToValueAtTime(0.02, startTime + item.duration * 0.9);
                osc.connect(gain);
                gain.connect(santaAudioContext.destination);
                osc.start(startTime);
                osc.stop(startTime + item.duration);
                santaOscillators.push(osc);

                // Add soft bell harmonics
                const bell = santaAudioContext.createOscillator();
                const bellGain = santaAudioContext.createGain();
                bell.type = 'sine';
                bell.frequency.value = noteFreq[item.note] * 2;
                bellGain.gain.setValueAtTime(0.03, startTime);
                bellGain.gain.exponentialRampToValueAtTime(0.001, startTime + item.duration * 0.5);
                bell.connect(bellGain);
                bellGain.connect(santaAudioContext.destination);
                bell.start(startTime);
                bell.stop(startTime + item.duration);
                santaOscillators.push(bell);

                startTime += item.duration;
            });

            // Gentle sleigh bells
            const loopStart = santaAudioContext.currentTime;
            for (let i = 0; i < 25; i++) {
                const bellTime = loopStart + (i * 0.3);
                if (bellTime < startTime) {
                    const sleigh = santaAudioContext.createOscillator();
                    const sGain = santaAudioContext.createGain();
                    sleigh.type = 'sine';
                    sleigh.frequency.value = 2500 + Math.random() * 500;
                    sGain.gain.setValueAtTime(0.02, bellTime);
                    sGain.gain.exponentialRampToValueAtTime(0.001, bellTime + 0.08);
                    sleigh.connect(sGain);
                    sGain.connect(santaAudioContext.destination);
                    sleigh.start(bellTime);
                    sleigh.stop(bellTime + 0.08);
                    santaOscillators.push(sleigh);
                }
            }

            const loopDuration = startTime - santaAudioContext.currentTime;
            if (santaIsPlaying) {
                setTimeout(() => {
                    startTime = santaAudioContext.currentTime;
                    playMelodyLoop();
                }, loopDuration * 1000);
            }
        }

        playMelodyLoop();
    } catch (e) {
        console.log('Audio not supported');
    }
}

function stopJingleBells() {
    santaIsPlaying = false;
    santaOscillators.forEach(osc => {
        try { osc.stop(); } catch(e) {}
    });
    santaOscillators = [];
    if (santaAudioContext) {
        santaAudioContext.close();
        santaAudioContext = null;
    }
}
